import { Item } from "./components/fade-in";
import { List } from "./components/list";
import Link from "next/link";
import { Footer } from "./components/footer";

export default function HomeLayout() {
  return (
    <>
      <div className="max-w-screen relative z-50 mx-auto h-full px-4 pt-20 md:h-screen">
        <div>
          <Item translateAnimation={false}>
            <h1 className="text-lg font-semibold">Mehdi Ben Hadj Ali</h1>
          </Item>
          <Item>
            <p className="text-fg-muted">Design Engineer</p>
          </Item>
          <Item>
            <p className="mt-10 text-sm">
              Currently making the web better at{" "}
              <Link href="https://dotui.org" target="_blank">
                dotUI
              </Link>
              .<br />I like building accessible, polished, and modern user
              interfaces.
            </p>
          </Item>
          <Item>
            <Footer />
          </Item>
        </div>
      </div>
    </>
  );
}

"use client";

import { motion, HTMLMotionProps, type Variants } from "framer-motion";

const container: Variants = {
  hidden: {},
  show: {
    transition: {
      staggerChildren: 0.1,
    },
  },
};

const item = (y: boolean): Variants => ({
  hidden: {
    opacity: 0,
    y: y ? 16 : 0,
    filter: "blur(10px)",
  },
  show: {
    opacity: 1,
    scale: 1,
    y: 0,
    filter: "blur(0px)",
    transition: {
      type: "spring",
      stiffness: 150,
      damping: 19,
      mass: 1.2,
    },
  },
});

function Container(props: HTMLMotionProps<"div">) {
  return (
    <motion.div
      variants={container}
      initial="hidden"
      animate="show"
      {...props}
    />
  );
}

function Item({
  translateAnimation = true,
  ...props
}: { translateAnimation?: boolean } & HTMLMotionProps<"div">) {
  return <motion.div variants={item(translateAnimation)} {...props} />;
}

export { Container, Item };

import Link from "next/link";

const links = [
  { label: "GitHub", href: "https://github.com/mehdibha" },
  { label: "X", href: "https://x.com/mehdibha_" },
  { label: "LinkedIn", href: "https://www.linkedin.com/in/mehdibha/" },
  { label: "Cal", href: "https://cal.com/mehdibha" },
];

export const Footer = () => {
  return (
    <div className="my-20">
      <div className="text-fg-muted flex flex-col gap-2 border-t py-6 text-sm">
        <div className="text-fg-muted flex items-center gap-3 text-sm">
          <div>Find me on:</div>
          {links.map((link) => (
            <Link key={link.label} href={link.href} target="_blank">
              {link.label}
            </Link>
          ))}
        </div>
        <div>Or mail me at hello@mehdibha.com</div>
      </div>
    </div>
  );
};

"use client";

import { useState } from "react";
import { Link } from "next-view-transitions";
import { cn } from "@/lib/utils";
import { Item } from "./fade-in";

export const List = ({
  items,
}: {
  items: { title: string; description?: string; hint?: string; href: string }[];
}) => {
  const [hoveredIndex, setHoveredIndex] = useState<number | null>(null);

  return (
    <div className="text-sm">
      {items.map((item, index) => (
        <Item
          key={index}
          onHoverStart={() => {
            setHoveredIndex(index);
          }}
          onHoverEnd={() => setHoveredIndex(null)}
        >
          <Link
            href={item.href}
            target={item.href.startsWith("http") ? "_blank" : undefined}
            className="w-full"
          >
            <div
              className={cn(
                "flex items-center justify-between border-b py-3 duration-200",
                hoveredIndex !== null && hoveredIndex !== index
                  ? "!opacity-50"
                  : "",
              )}
            >
              <div className="flex flex-col">
                <span>{item.title}</span>
                {item.description && (
                  <span className="text-fg-muted">{item.description}</span>
                )}
              </div>
              {item.hint && (
                <span className="text-fg-muted text-right">{item.hint}</span>
              )}
            </div>
          </Link>
        </Item>
      ))}
    </div>
  );
};
